import React from "react";
import {randomNb} from "./Number";
import './Home.css';

export default class Home extends React.Component {

	state = {
		number: null
	}

	change = event => {
		this.setState({
			[event.target.id]:event.target.value
		});
	};

	submit = event => {
		event.preventDefault();
		console.log(this.state)
	};

	render() {
		var nbToFind = randomNb(101);
		var infos = "";

		return (

			<div className="container">
				<div className="card">
					<div class="txt">
						<form onSubmit={this.submit}>
							<label>Entrez un nombre entre 0 et 100 :</label>
							<input type="text" value="number" onChange={this.change}/>
							<button id="submit-btn" type="submit" >Envoyer</button>
							<button id="restart-btn">Recommencer</button>
						</form>
						<div className="reponse">
							<p>{this.state} {nbToFind}</p>
						</div>
					</div>
				</div>
			</div>
		);
	}
}
