import React from 'react';
import { Provider } from 'react-redux';
import { createStore } from 'redux';
import reducer from './redux/reducers'
import './App.css';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

import Home from './component/Home';
import Students from './component/Students';
import Student from './component/Students/Student';
import {randomNb} from './component/Number';

import {
  addStudent,
  editStudent,
  delStudent
} from './redux/actions';

const store = createStore(reducer);


export default class App extends React.Component {
    constructor(props) {
      super(props);
      this.nbToFind = randomNb(101);

    this.routes = [
      {
        path: '/',
        text: 'Accueil',
        component: Home
      },
      {
        path: '/students',
        text: 'Etudiants',
        component: Students,
        routes: [
          {
            path: 'student?add',
            text: 'Ajouter',
            component: Student
          },
          {
            path: 'student?edit',
            text: 'Ajouter',
            component: Student
          }
        ]
      }

    ];

    console.log('initial state', store.getState());

    const unsubscribe = store.subscribe(() => console.log(store.getState()));

    store.dispatch(addStudent({
      name: 'Anga',
      age: 32,
      groupe: 1
    }));

    unsubscribe();
  }

  render() {
    const {routes} = this;
    return (

      <Provider store={store}>
        <Router>
          <div>
            <ul>
              {routes.map((route, i) =>
                (
                  <li key={i}><Link to={route.path}>{route.text}</Link></li>
                )
              )}
            </ul>

            <div className="container">
              <div className="app">
                <Switch>
                  {routes.map((route, i) =>
                      (
                          <Route key={i} exact path={route.path}><route.component /></Route>
                      )
                  )}
                </Switch>
                <form>
                  <label>Entrez un nombre entre 0 et 100 :</label>
                  <input type="number" name="number"/>
                  <button id="submit-btn" type="submit" >Envoyer</button>
                  <button>Recommencer</button>
                </form>
                <div className="reponse">
                  <p>Le nombre à trouver est : {this.nbToFind.value = randomNb(101)}</p>
                </div>
              </div>
            </div>

          </div>
        </Router>
      </Provider>
    );
  }
}
