import React from "react";
import {randomNb} from "./Number";
import './Home.css';

export default class Home extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			numberToFind: null
		}
	}

	handleNumberToFindChange = (event) => {
		this.setState({
			numberToFind: event.target.value
		})
	}

	handleSubmit = event ={

	}
	render() {
		var nbToFind = randomNb(101);

		return (

			<div className="container">
				<div className="card">
					<div class="txt">
						<form onSubmit={this.handleSubmit}>
							<label>Entrez un nombre entre 0 et 100 :</label>
							<input type="number" value="this.state.numberToFind" onChange={this.HandleNumberToFindChange}/>
							<button id="submit-btn" type="submit" >Envoyer</button>
							<button id="restart-btn">Recommencer</button>
						</form>
						<div className="reponse">
							<p>{this.state} {nbToFind}</p>
						</div>
					</div>
				</div>
			</div>
		);
	}
}
