import React from "react";
import {randomNb} from "./Number";
import './Home.css';

export default class Home extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			numberToFind: 'undifined'
		}
	}

	change = e =>{
		this.setState({
			[e.target.name]: e.target.value
		})
	};

	onSubmit = () => {
		console.log(this.state);
	}

	render() {
		var nbToFind = randomNb(101);

		return (

			<div className="container">
				<div className="card">
					<div class="txt">
						<form>
							<label>Entrez un nombre entre 0 et 100 :</label>
							<input type="number" value={this.state.numberToFind} onChange={e => this.change(e)}/>
							<button id="submit-btn" type="submit" onClick={() => this.onSubmit()} >Envoyer</button>
							<button id="restart-btn">Recommencer</button>
						</form>
						<div className="reponse">
							<p>{nbToFind}</p>
						</div>
					</div>
				</div>
			</div>
		);
	}
}
