import React from "react";
import {randomNb} from "./Number";
import './Home.css';

export default class Home extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			entry: ''
		}
	}

	handleInputChange = (event) => {
		this.props.entry(event.target.value);
	};

	render() {
		var nbToFind = randomNb(101);
		var infos = "";
		const{entry} = this.state;

		return (

			<div className="container">
				<div className="card">
					<div class="txt">
						<form>
							<label>Entrez un nombre entre 0 et 100 :</label>
							<input type="number" value="entry" onChange={this.handleInputChange}/>
							<button id="submit-btn" type="submit" >Envoyer</button>
							<button id="restart-btn">Recommencer</button>
						</form>
						<div className="reponse">
							<p>{infos} {nbToFind}</p>
						</div>
					</div>
				</div>
			</div>
		);
	}
}
