import React from "react";
import './Score.css';
import './HomeH;'


export default class Score extends React.Component {

    state = {
    scoreList:[]
    };

    addScore = score => {
        let scores = [...this.state.scoreList, score];
        this.setState()
    }

    render () {

        return (
            <div className="container-score">
                <div className="card-score">
                    <div class="txt-score">
                        <p><span>Scores</span></p>
                        <p>Teddy</p>
                        <p>Hugo</p>
                        <p>Ivann</p>
                    </div>
                </div>
            </div>
        );
    }
}
