import React from "react";
import {randomNb} from "./Number";
import './Home.css';

export default class Home extends React.Component {

		state = {
			numberToFind: randomNb(101),
			entry: ''
		};

	change = e =>{
		this.props.onChange({ [e.target.name]: e.target.value });
		this.setState({
			[e.target.name]: e.target.value
		});
	};

	onSubmit = (e) => {
		e.preventDefault();
		this.setState({
			entry: '',
		});
		this.props.onChange({
			entry: '',
		});
	};

	render() {

		return (

			<div className="container">
				<div className="card">
					<div class="txt">
						<form>
							<label>Entrez un nombre entre 0 et 100 :</label>
							<input placeholder='entry' value={this.state.entry} onChange={e => this.change(e)}/>
							<button id="submit-btn" type="submit" onClick={this.onSubmit()} >Envoyer</button>
							<button id="restart-btn">Recommencer</button>
						</form>
						<div className="reponse">
							<p>A trouver : {this.state.numberToFind}, rentr√© : {this.state.entry} </p>
						</div>
					</div>
				</div>
			</div>
		);
	}
}
